trans-unit.source:
let list: number[] = []
input.onButtonPressed(Button.A, () =&gt; {
    for (let i = 0; i &lt;= list.length - 1; i++) {
        for (let j = 0; j &lt;= list[i] - 1; j++) {
            led.plot(Math.random(5), Math.random(5))
        }
        basic.pause(1000)
        basic.clearScreen()
    }
})
list = [5, 2, 1, 3, 4]

