trans-unit.source:
let temp = 0
let j = 0
let min = 0
let row = 0
let list: number[] = []
let item = 0
let column = 0
input.onButtonPressed(Button.B, () =&gt; {
    basic.clearScreen()
    for (let column = 0; column &lt;= 4; column++) {
        row = 0
        while (row &lt; list[column]) {
            led.plot(column, 4 - row)
            row += 1
        }
    }
})
input.onButtonPressed(Button.AB, () =&gt; {
    for (let index = 0; index &lt;= 4; index++) {
        list[index] = Math.random(5) + 1
    }
})
input.onButtonPressed(Button.A, () =&gt; {
    item = 1
    for (let i = 0; i &lt;= 3; i++) {
        min = i
        for (let inner = 0; inner &lt;= 3 - i; inner++) {
            j = inner + item
            if (list[j] &lt; list[min]) {
                min = j
            }
        }
        if (min != i) {
            temp = list[min]
            list[min] = list[i]
            list[i] = temp
        }
        item += 1
        basic.clearScreen()
        for (let column = 0; column &lt;= 4; column++) {
            row = 0
            while (row &lt; list[column]) {
                led.plot(column, 4 - row)
                row += 1
            }
            basic.pause(100)
        }
    }
})
basic.showLeds(`
    .

