trans-unit.source:
let j = 0
let row = 0
let element = 0
let list: number[] = []
input.onButtonPressed(Button.A, () =&gt; {
    for (let i = 0; i &lt;= 4; i++) {
        element = list[i]
        j = i
        while (j &gt; 0 &amp;&amp; list[j - 1] &gt; element) {
            list[j] = list[j - 1]
            j += -1
            list[j] = element
        }
        basic.clearScreen()
        for (let column2 = 0; column2 &lt;= 4; column2++) {
            row = 0
            while (row &lt; list[column2]) {
                led.plot(column2, 4 - row)
                row += 1
            }
            basic.pause(100)
        }
    }
})
