trans-unit.source:
let reverse = 0
let speed = 10
let inner = 0
let outer = 0
basic.forever(() =&gt; {
    for (let outer = 0; outer &lt;= 4; outer++) {
        reverse = 4 - outer
        for (let inner = 0; inner &lt;= 4; inner++) {
            led.plot(outer, reverse)
            basic.pause(speed)
            led.plot(reverse, outer)
            basic.pause(speed)
            led.plot(reverse - inner, reverse)
            basic.pause(speed)
            led.plot(reverse, reverse - inner)
            basic.pause(speed)
        }
    }
    for (let outer = 0; outer &lt;= 4; outer++) {
        reverse = 4 - outer
        for (let inner = 0; inner &lt;= 4; inner++) {
            led.unplot(outer, reverse)
            basic.pause(speed)
            led.unplot(reverse, outer)
            basic.pause(speed)
            led.unplot(reverse - inner, reverse)
            basic.pause(speed)
            led.unplot(reverse, reverse - inner)
            basic.pause(speed)
        }
    }
})

