trans-unit.source:
let item: game.LedSprite = null
item = game.createSprite(0, 0)

input.onButtonPressed(Button.A, () =&gt; {
   for (let i = 0; i &lt; 4; i++) {
       item.move(4)
       item.turn(Direction.Right, 90)
   }
})

