trans-unit.source:
let binary = ""
let decimal = 0
input.onButtonPressed(Button.A, () =&gt; {
    binary = binary + "1"
    basic.showString(binary)
    decimal = decimal * 2 + 1
})
input.onButtonPressed(Button.B, () =&gt; {
    binary = binary + "0"
    basic.showString(binary)
    decimal = decimal * 2
})
input.onButtonPressed(Button.AB, () =&gt; {
    basic.showNumber(decimal)
})
decimal = 0
binary = ""

