trans-unit.source:
let p2 = 0
let p1 = 0
input.onButtonPressed(Button.A, () =&gt; {
    p1 += 1
    basic.showNumber(p1)
})
input.onButtonPressed(Button.B, () =&gt; {
    p2 += 1
    basic.showNumber(p2)
})
input.onGesture(Gesture.Shake, () =&gt; {
    if (Math.random(p1 + p2 - 1 + 1) + 1 &lt;= p1) {
        basic.showString("A")
    } else {
        basic.showString("B")
    }
})

trans-unit.target:
